import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItems";
import Card from "../UI/Card";
import ExpensesFilter from "../EXPENSEFILTER/ExpensesFilter";

function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2020");
  const yearSelectionHandler = (selectedYear) => {
    console.log(selectedYear);
    setFilteredYear(selectedYear);
  };
  const items = props.exp.map((obj) => {
    return (
      <ExpenseItem
        key={obj.id}
        title={obj.title}
        amount={obj.amount}
        date={obj.date}
      />
    );
  });
  console.log(props.exp);
  const checkYear = (expe) => {
    const year = expe.date.getFullYear();
    year === selectedYear;
  };
  const result = props.exp.filter(checkYear);

  return (
    <div>
      <ExpensesFilter
        selected={filteredYear}
        onYearSelection={yearSelectionHandler}
      />
      <Card className="expenses">{items}</Card>
    </div>
  );

